    GetDateFormat(receivedDate: any) {
        var self = this;
        var dayValue = (receivedDate.toString()).split("-")[2];
        var yearValue = (receivedDate.toString()).split("-")[0];
        var monthValue = "";
        switch((receivedDate.toString()).split("-")[1]){
            case "01": 
                monthValue = "January";
                break;
            case "02": 
                monthValue = "February";
                break;
            case "03": 
                monthValue = "March";
                break;
            case "04": 
                monthValue = "April";
                break;
            case "05": 
                monthValue = "May";
                break;
            case "06": 
                monthValue = "June";
                break;
            case "07": 
                monthValue = "July";
                break;
            case "08": 
                monthValue = "August";
                break;
            case "09": 
                monthValue = "September";
                break;
            case "10": 
                monthValue = "October";
                break;
            case "11": 
                monthValue = "November";
                break;
            case "12": 
                monthValue = "Decmeber";
                break;
            default:
                monthValue = "Invalid Month";
                break
            }

            return dayValue+" "+monthValue+" "+yearValue;
    }

    GetDateValues(dateValue): string{
        var self = this;
        return dateValue.split("/")[1] + "/" + dateValue.split("/")[0] + "/" + dateValue.split("/")[2];
    }
